        <script type="text/javascript">
            let gadget = null;
            (function () {
                gadget = AJS.Gadget({
                    baseUrl: "__ATLASSIAN_BASE_URL__",
                    useOauth: "/rest/gadget/1.0/currentUser",
                    id: 'my-gadget',
                    view: {
                        onResizeAdjustHeight: false,
					    onResizeReload: true,
                        template: function(args) {
                            var gadget = this;

                            var dashboardId = window.parent.location.search.match(/selectPageId=(\d+)/)[1];
                            console.log(dashboardId);
                            var gadgetId = gadget.getPrefs().getModuleId();
                            console.log("gadgetId " + gadgetId);

                            var htmlText = '<div class="demo-dfree"><div id="editor-container" class="dfree-body mce-content-body" contenteditable="true" style="position: relative;" spellcheck="false"></div><button id="save-button" onclick="saveText()">保存文本</button></div>';

                            //gadget.getView().html('<div class='demo-dfree'><div id="editor-container" contenteditable="true"></div><button id="save-button" onclick="saveText()">保存文本</button></div>');
                            gadget.getView().html(htmlText);

                            tinymce.init({
                                selector: '#editor-container',
                                menubar: false,
                                inline: true,
                                plugins: [
                                    'autolink',
                                    'codesample',
                                    'link',
                                    'lists',
                                    'media',
                                    'powerpaste',
                                    'table',
                                    'image',
                                    'quickbars',
                                    'codesample',
                                    'help'
                                ],
                                toolbar: false,
                                quickbars_insert_toolbar: 'quicktable image media codesample',
                                quickbars_selection_toolbar: 'bold italic underline | formatselect | blockquote quicklink',
                                contextmenu: 'undo redo | inserttable | cell row column deletetable | help',
                                powerpaste_word_import: 'clean',
                                powerpaste_html_import: 'clean',
                            }).then(function () {
                                AJS.$.ajax({
                                    url: '/rest/api/2/dashboard/' + dashboardId + '/items/' + gadgetId + '/properties/',
                                    type: 'GET',
                                    dataType: 'json',
                                    contentType: 'application/json',
                                    success: function(data) {
                                        var keys = data.keys;
                                        console.log(keys);
                                        var haveText = false;
                                        keys.forEach(function(key) {
                                            console.log(key);
                                            if(key.key=='text')
                                            {
                                                haveText = true;
                                            }
                                        });
                                        console.log("3333333333");

                                        if(haveText)
                                        {
                                            AJS.$.ajax({
                                                url: '/rest/api/2/dashboard/' + dashboardId + '/items/' + gadgetId + '/properties/' + 'text',
                                                type: 'GET',
                                                dataType: 'json',
                                                contentType: 'application/json',
                                                success: function(data) {
                                                    var text = data.value.value;
                                                    console.log("444444444444");
                                                    console.log(text);
                                                    var editor = tinymce.activeEditor;
                                                    editor.setContent(text);
                                                },
                                                error: function(xhr, status, error) {
                                                }
                                            });
                                        }
                                    },
                                    error: function(xhr, status, error) {
                                    }
                                });
                            });

                        },
                        args: [{
                            key: "versionData",
                            ajaxOptions: function() {
                                return {
                                    url: "/rest/jira-versions-gadget/1.0/versions.json"
                                };
                            }
                        }]
                    }
                });
            })();

   			function saveText() {
                // 此处是保存文本的代码...
                var editor = tinymce.activeEditor;
                var content = editor.getContent();


                console.log('当前文本内容2: ' + content);

                var dashboardId = window.parent.location.search.match(/selectPageId=(\d+)/)[1];
                console.log(dashboardId);
                var gadgetId = gadget.getPrefs().getModuleId();
                console.log("gadgetId " + gadgetId);

                AJS.$.ajax({
                    url: '/rest/api/2/dashboard/' + dashboardId + '/items/' + gadgetId + '/properties/' + 'text',
                    type: 'PUT',
                    contentType: 'application/json',
                    dataType: 'json',
                    data: JSON.stringify({ 'value': content }),
                    success: function() {
                        console.log('Data saved successfully.');
                    },
                    error: function(xhr, status, error) {
                        console.error('Failed to save data:', status, error);
                    }
                });
            }

            function getDashboardId() {
                var searchParams = new URLSearchParams(window.location.search);
                console.log(searchParams);
                var dashboardId = searchParams.get('dashboardId');
                return dashboardId;
            }

            function adjustIframeHeight(newHeight) {
                var iframe = window.frameElement;
                console.log(iframe);

                var height = document.documentElement.scrollHeight;
                iframe.style.height = newHeight + 'px';
                iframe.contentWindow.location.reload();
            }

            function resizeGadget() {
                // 获得可编辑文本框的高度
                var textHeight = AJS.$('#editable-text')[0].scrollHeight;
                console.log('当前高度为：', textHeight);
                // 通过设置新的高度来调整 Gadget 的大小
                var newHeight = textHeight + 50;
                gadget.resize(AJS.$('#editor-container').width(), textHeight + 50);


                // 调整所属iframe的高度
                function adjustIframeHeight() {
                    var iframe = window.frameElement;
                    console.log(iframe);

                    var height = document.documentElement.scrollHeight;
                    iframe.style.height = newHeight + 'px';
                    iframe.contentWindow.location.reload();
                }
                //adjustIframeHeight();


            }
        </script>
